import { createSlice } from "@reduxjs/toolkit";

const cartSlice = createSlice({
  name: "cart",
  initialState: { cartShow: false },
  reducers: {
    cartToggle(state) {
      state.cart.cartShow = !state.cart.cartShow;
    },
  },
});

export default cartSlice;
